import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import Home from "../pages/Index";
import Gallery from '../pages/Gallery';
import LoginForm from './LoginForm';
import LogoutScreen from './LogoutScreen'; // Import the LogoutScreen component

function Login() {
    const [loggedIn, setLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [redirectToLogin, setRedirectToLogin] = useState(false); // State for redirection

    const handleLogin = () => {
        if (username === 'admin' && password === 'admin') {
            setLoggedIn(true);
            setRedirectToLogin(false); // Reset redirection state after successful login
        } else {
            alert("Invalid username or password");
        }
    };

    const handleLogout = () => {
        setLoggedIn(false);
        setUsername('');
        setPassword('');
        setRedirectToLogin(true); // Set redirection state on logout
    };

    return (
        <Router>
            <div>
                <div>
                    <ul className="navigation">
                        {loggedIn ? (
                            <>
                                <li>
                                    <Link to="/home">Home</Link>
                                </li>
                                <li>
                                    <Link to="/gallery">Gallery</Link>
                                </li>
                                <li>
                                    <Link to="/logout" onClick={handleLogout}>Logout</Link>
                                </li>
                            </>
                        ) : (
                            <li>
                                <h1 style={{color: "#1f64fd" }}>KittyDeluxe</h1>
                            </li>
                        )}
                    </ul>
                    <Routes>
                        <Route path="/" element={<LoginForm handleLogin={handleLogin} username={username} setUsername={setUsername} password={password} setPassword={setPassword} />} />
                        <Route path="/home" element={loggedIn ? <Home /> : null} />
                        <Route path="/gallery" element={loggedIn ? <Gallery /> : null} />
                        {redirectToLogin && <Route path="/logout" element={<LogoutScreen />} />} {/* Render LogoutScreen when redirected to logout */}
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default Login;
