import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import Home from "../pages/Index";
import Gallery from '../pages/Gallery';
import LoginForm from './LoginForm'; // Import the LoginForm component

function Login() {
    const [loggedIn, setLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = () => {
        if (username === 'admin' && password === 'admin') {
            setLoggedIn(true);
        } else {
            alert("Invalid username or password");
        }
    };

    const handleLogout = () => {
        setLoggedIn(false);
        setUsername('');
        setPassword('');
    };

    return (
        <Router>
            <div>
                <div>
                    <ul className="navigation">
                        {loggedIn ? (
                            <>
                                <li>
                                    <Link to="/home">Home</Link>
                                </li>
                                <li>
                                    <Link to="/gallery">Gallery</Link>
                                </li>
                                <li>
                                    <Link to="/logout" onClick={handleLogout}>Logout</Link>
                                </li>
                            </>
                        ) : (
                            <li>
                                <Link to="/">Login</Link>
                            </li>
                        )}
                    </ul>
                    <Routes>
                        <Route path="/" element={<LoginForm handleLogin={handleLogin} username={username} setUsername={setUsername} password={password} setPassword={setPassword} />} />
                        <Route path="/home" element={loggedIn ? <Home /> : <Navigate to="/" />} />
                        <Route path="/gallery" element={loggedIn ? <Gallery /> : <Navigate to="/" />} />
                        <Route path="/logout" element={<Navigate to="/" />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default Login;
