import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Home from "../pages/Index";
import Gallery from '../pages/Gallery';
import LoginForm from './LoginForm';
import LogoutScreen from './LogoutScreen';

function Login() {
    const [loggedIn, setLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [redirectToHome, setRedirectToHome] = useState(false); // State for redirection

    const handleLogin = () => {
        if (username === 'admin' && password === 'admin') {
            setLoggedIn(true);
            setRedirectToHome(true); // Set redirect to true after successful login
        } else {
            alert("Invalid username or password");
        }
    };

    const handleLogout = () => {
        setLoggedIn(false);
        setUsername('');
        setPassword('');
        setRedirectToHome(false); // Reset redirection state on logout
    };

    return (
        <Router>
            <div>
                <div class = "nav">
                        {loggedIn ? (
                            <>
                            <ul class ="nav-item">
                                <li class = "nav-link">
                                    <h1 style={{color: "#1f64fd"}}>KittyDeluxe</h1>
                                </li>
                                <li class = "nav-link">
                                    <Link to="/home">Home</Link>
                                </li>
                                <li class = "nav-link">
                                    <Link to="/gallery">Gallery</Link>
                                </li>
                                <li class = "nav-link">
                                    <Link to="/logout" onClick={handleLogout}>Logout</Link>
                                </li>
                            </ul>
                            </>
                        ) : (
                            <ul class ="nav-item">
                                <li>
                                    <h1 style={{color: "#1f64fd"}}>KittyDeluxe</h1>
                                </li>
                                <li>
                                    <Link to="/">Login</Link>
                                </li>
                            </ul>
                        )}
                    <Routes>
                        {/* Redirect to home page if logged in */}
                        {redirectToHome && <Route path="/" element={<Home />} />}
                        <Route path="/" element={<LoginForm handleLogin={handleLogin} username={username} setUsername={setUsername} password={password} setPassword={setPassword} />} />
                        <Route path="/home" element={loggedIn ? <Home /> : null} />
                        <Route path="/gallery" element={loggedIn ? <Gallery /> : null} />
                        <Route path="/logout" element={loggedIn ? <Logout handleLogout={handleLogout} /> : <LogoutScreen/>} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

function Logout({ handleLogout }) {
    handleLogout();
    return null; // Renders nothing
}

export default Login;
