import React, { useState } from "react";

const ImageGallery = () => {
    const numberOfImages = 35; // Adjust the number of images as needed
    const imageNames = Array.from({ length: numberOfImages }, (_, index) => `kitty${index + 1}.png`);
    const [isBlurred, setIsBlurred] = useState(false);

    const toggleBlur = () => {
        setIsBlurred(prevState => !prevState);
    };

    return (
        <div className="kitty"
        style={{ position: "relative" }}>
            <h1>KITTY NFT GALLERY</h1>
            <button onClick={toggleBlur}>
                {isBlurred ? "Show Images" : "Find out More!"}
                </button>
            <br/>
            {imageNames.map((imageName, index) => (
                <div key={index} style={{ position: "relative", display: "inline-block", margin: "10px" }}>
                    <img 
                        src={require(`../Assets/${imageName}`)} 
                        alt={`Kitty${index + 1}`} 
                        style = {{
                            width: "250px",
                            height: "250px",
                            filter: isBlurred ? "blur(5px)" : "none",
                            margin: "auto"
                        }}
                        
                    />
                    {isBlurred && (
                        <p 
                            style={{
                                position: "absolute",
                                top: "50%",
                                left: "50%",
                                transform: "translate(-50%, -50%)",
                                backgroundColor: "rgba(255, 255, 255, 0.7)",
                                padding: "10px",
                                borderRadius: "5px"
                            }}
                        >
                            This is Kitty {index + 1} 
                        </p>
                    )}
                </div>
            ))}
        </div>
    );
};

export default ImageGallery;
